//Start by deleting all active scheduled events for this incident if there are any
deleteEvents();

function deleteEvents(){
	var gr = new GlideRecord('sysevent');
	gr.addQuery('instance', current.sys_id);
	gr.addQuery('processed', '');
	gr.addQuery('state', 'ready');
	gr.addQuery('name', '!=', 'incident.update.notification');
	gr.addQuery('name', '!=', 'incident.worknoted');
	gr.query();
	while (gr.next()) {
		gr.deleteRecord();
	}
}

