closeChild();

function closeChild(){
	var close_code = current.close_code;
	var close_notes = current.close_notes;
	
	//Look for all child incidents related to this parent incident.
	//Copy close code and notes from parent to child and resolve it.
	var gr = new GlideRecord('incident');
	gr.addQuery('parent_incident', current.sys_id);
	gr.addQuery('active', 'true');
	gr.query();
	while (gr.next()) {
		gr.state.setValue('6');
		gr.close_code = close_code;
		gr.close_notes = close_notes;
		gr.hold_reason = '';
		gr.update();
	}
}

